mapper.select:
SELECT *
FROM (SELECT ROWNUM RN, C.*
	  FROM (SELECT *
	        FROM V_CLASSBEAN
	        WHERE 1=1

	  	    AND CATE1NO = #{cate1No}


	  		AND CATE2NO = #{cate2No}




	  				AND UPPER(CLSKEYWORD) LIKE '%' || UPPER(#{word}) || '%'



	  		ORDER BY CLSOPENDATE DESC
	  		) C
	 )
WHERE RN BETWEEN #{pageNo}*#{pageSize}-#{pageSize}+1 AND #{pageNo}*#{pageSize}
