mapper.update:
UPDATE CURRI
SET FILENO = #{fileNo}
WHERE CURNO = #{curNo}

UPDATE CURRI 
SET FILENO=#{fileNo}, CURTITLE=#{curTitle}, CURSTARTDATE=#{curStartDate}, CURENDDATE=#{curEndDate}, CURDISC=#{curDisc} 
WHERE CURNO=#{curNo}

UPDATE CURRI 
SET CURTITLE=#{curTitle}, CURSTARTDATE=TO_DATE(#{curStartDate}, 'YYYY-MM-DD HH24:MI:SS'), CURENDDATE=TO_DATE(#{curEndDate}, 'YYYY-MM-DD HH24:MI:SS'), CURDISC=#{curDisc} 
WHERE CURNO=#{curNo}
