mapper.select:
SELECT * FROM V_CLASSBEAN
WHERE MBID=#{mbid}
WHERE CLSNO IN ( SELECT C.CLSNO FROM CLASS C JOIN ( SELECT STUDENT.CLSNO CLSNO FROM STUDENT WHERE STUDENT.MBID = #{mbid} ) S ON C.CLSNO = S.CLSNO )

mapper.update:
UPDATE MEMBER 
SET MBPWD = #{mbPwd} 
WHERE MBID = #{mbId}

UPDATE TEACHER 
SET FILENO=#{fileNo}, TEACHERINTRO=#{teacherIntro}, TEACHERDETAIL=#{teacherDetail} 
WHERE MBID=#{mbId}

UPDATE TEACHER 
SET FILENO=NULL, TEACHERINTRO=NULL, TEACHERDETAIL=NULL 
WHERE MBID=#{mbId}
